mapper.update:

update board
 set order_no = order_no+1
where group_no = #{groupNo}
 and order_no>= #{orderNo}

mapper.select:
select a.no, a.content, a.group_no as groupNo, a.order_no as orderNo, a.depth, a.user_no as userNo, b.name as userName from board a, user b where a.group_no=#{groupNo} and a.depth>0 and a.user_no=b.no
and no < #{map.startNo}
order by order_no asc limit 0, 5

mapper.update:

update board
 set hit = hit+1
where no=#{no}


update board
 set title=#{title},
 	 content=#{content}
 where no=#{no} and user_no=#{userNo}
