mapper.select:

select name 
 from reply
where no=#{replyNo};

select no, name, message, date_format(reg_date, '%Y-%m-%d') as regDate, group_no as groupNo, order_no as orderNo, reply_no as replyNo, depth from reply where board_no = #{boardNo}
and no > #{startNo}
order by group_no desc, order_no desc limit 0, 5

mapper.update:

update reply
 set order_no = order_no +1
where group_no = #{groupNo}
 and order_no>= #{orderNo}

mapper.select:

select group_no
 from reply
where no=#{no}

mapper.delete:

delete 
 from reply
where no = #{no} and passwd=#{password}
